mapper.insert:
INSERT /* PK=biz.tech.mapif.em.PGEM0090Mapper.insertMember */ 
INTO		tb_member_info ( 
				PK								/* 사원번호 */
			, name							/* 이름 */
			, rank							/* 직급 */
			, ss_num						/* 주민번호 */
			, bank							/* 결제은행 */
			, bank_num						/* 결제계좌 */
			, in_date						/* 입사일 */
			, tel							/* 연락처 */
			, p_mail						/* 개인메일 */
			, c_mail						/* 회사메일 */
			, address						/* 주소 */
)
VALUES ( 
		  #{adPK}
		, #{adname}
		, #{adrank}
		, #{adss}
		, #{adbank}
		, #{adbanknum}
		, #{startdt}
		, #{adtel}
		, #{adpmail}
		, #{adcmail}
		, #{adadd}
)

mapper.update:
UPDATE /* PK=sqlmpa.cm.PGMS0117Mapper.updatemember */
		tb_member_info
SET
		PK = #{adPK}										
		, name = #{adname}								
		, rank = #{adrank}								
		, ss_num = #{adss}								
		, bank = #{adbank}								
		, bank_num = #{adbanknum}						
		, in_date = #{startdt}							
		, tel = #{adtel}
		, p_mail = #{adpmail}
		, c_mail = #{adcmail}
		, address = #{adadd}								
WHERE PK = #{adPK}

mapper.select:
SELECT /* PK=biz.tech.mapif.em.PGEM0090Mapper.findmember */ PK /* 사원번호 */ , name /* 이름 */ , rank /* 직급 */ , ss_num AS ssnum /* 주민번호 */ , bank /* 결제은행 */ , bank_num AS banknum /* 결제계좌 */ , in_date AS indate /* 입사일 */ , tel /* 연락처 */ , p_mail AS pmail /* 개인메일 */ , c_mail AS cmail /* 회사메일 */ , address /* 주소 */ FROM tb_member_info
name=#{ad_search_word}
ORDER BY PK ASC
